mapper.select:
select

from saas_supplier
where id = #{id,jdbcType=BIGINT}

SELECT ss.id as id,
ss.code as code,
ss.name as name
FROM saas_supplier ss

  ss.enterprise_id = #{enterpriseId,jdbcType=BIGINT}
  and ss.owner_id =  #{ownerId,jdbcType=BIGINT}
  and ss.valid_flag = 1
  
  and ss.approve_status = #{approveStatus,jdbcType=INTEGER}
  
  and ss.status = #{status,jdbcType=INTEGER}
  
    and (
    INSTR(ss.name,#{param})
    or INSTR(ss.code,#{param})
    or INSTR(ss.pinyin,#{param})
    )
  

order by id desc

select

from saas_supplier
where enterprise_id = #{enterpriseId,jdbcType=BIGINT}

  and owner_id IN
  
    #{item}
  

and status = '1'
and valid_flag = '1'

 and approve_status = #{approveStatus,jdbcType=INTEGER}

select
from saas_supplier where id IN
#{item}

SELECT  FROM saas_supplier supplier WHERE
code = #{code}

  
    
      and supplier.enterprise_id = #{paramVO.enterpriseId}
      and supplier.owner_id = #{paramVO.enterpriseId}
    
  
  
    
      and supplier.enterprise_id = #{paramVO.parentId}
      and supplier.owner_id in (#{paramVO.enterpriseId})
    
  
  
    
      and supplier.enterprise_id = #{paramVO.enterpriseId}

SELECT ss.id as id, ss.enterprise_id as enterpriseId, ss.owner_id as ownerId, ss.chain_type as ownerChainType, ss.code as code, ss.name as name, ss.nature as nature, ss.business_man_name as businessManName, ss.tel as tel, ss.fax as fax, ss.company_address as companyAddress, ss.status as status, ss.approve_status as approveStatus FROM saas_supplier ss, saas_supplier_business sb, saas_enterprise en WHERE ss.enterprise_id = sb.enterprise_id and sb.supplier_id = ss.id
and ss.owner_id = en.id
and ss.enterprise_id = #{enterpriseId,jdbcType=BIGINT}
and ss.owner_id IN
#{item}
and ( INSTR(ss.code,#{param}) or INSTR(ss.name,#{param}) or INSTR(ss.pinyin,#{param}) )
and ss.nature = #{nature}
and ss.group_id = #{groupId}
and INSTR(ss.business_man_name,#{businessName})
and INSTR(sb.purchaser_name,#{purchaseName})
and ss.chain_type = #{ownerChainType}
and INSTR(en.code,#{ownerCode})
and INSTR(en.name,#{ownerName})
and (ss.approve_status = 0 or ss.approve_status = -1)
and ss.approve_status = #{approveStatus,jdbcType=INTEGER}
order by ss.id desc
limit #{start},#{pageSize}

select

from saas_supplier
where id = #{id}

select s.code as code, s.name as name, s.economic_type as economicType, s.tel as tel, s.fax as fax, s.company_address as companyAddress, s.status as status, s.valid_flag as validFlag, s.business_mode as businessMode, s.registered_capital as registeredCapital, s.postcode as postcode, s.email as email, s.site as site, s.storage_address as storageAddress, s.sale_man_name as saleManName, s.business_man_name as businessManName, s.legal_man_name as legalManName, s.quality_officer_name as qualityOfficerName, s.bank_name as bankName, s.bank_account as bankAccount, s.bank_account_name as bankAccountName, s.business_variety as businessVariety, s.business_scope_id as businessScopeId from saas_supplier s , saas_supplier_business sb WHERE s.enterprise_id = sb.enterprise_id and sb.supplier_id = s.id and s.enterprise_id = #{enterpriseId,jdbcType=BIGINT}
and s.owner_id IN
#{item}
and ( INSTR(s.code,#{param}) or INSTR(s.name,#{param}) or INSTR(s.pinyin,#{param}) )
and s.nature = #{nature}
and s.group_id = #{groupId}
and INSTR(s.business_man_name,#{businessName})
and INSTR(sb.purchaser_name,#{purchaseName})
and (s.approve_status = 0 or s.approve_status = -1)
and s.approve_status = #{approveStatus,jdbcType=INTEGER}
order by s.id desc

mapper.update:
update saas_supplier
set group_id = null
where group_id = #{id}

update saas_supplier
set group_id = null
where id = #{id}

update saas_supplier
set approve_status = #{approvalStatus}
where id = #{id}

