mapper.select:
select b.goods_id, d.name enterprise_code, d.code enterprise_name, a.order_date, a.code as discode, a.distr_unit_code, a.distr_unit_name, a.base_order_code, f.base_order_code distr_send_code, a.distr_type, a.`status`, b.quantity, b.unit_price, b.line_discount, b.amount, b.whole_discount, b.line_discount_amount, b.real_price, b.real_amount, b.tax_rate, b.notax_real_price, b.notax_real_amount, b.tax_amount, c.code, c.barcode, c.name, c.generic_name, c.dosage_id, c.dosage_name, c.unit_id, c.unit_name, c.specification, c.manufacturer, c.manufacturer_id, c.place, c.approval_number, c.business_variety, e.name as category_name, c.goods_attribute, c.domestic_import, c.management_scope_name, c.special_drug, c.in_charge_drug, c.limit_quantity, c.storage_temp, c.storage_condition_name, c.quality_period, c.quality_period_unit from saas_distr_in_notice a INNER join saas_distr_in_notice_detail b on b.order_id=a.id INNER join saas_distr_out f on f.code=a.base_order_code INNER join saas_goods c on c.id=b.goods_id INNER join saas_enterprise d on d.id=a.enterprise_id INNER join saas_goods_category e on e.id=c.category_id
order by ${sort}
limit #{pageNo},#{pageSize}

SELECT goods.id as id,goods.code as goodsCode,goods.name as goodsName, goods.generic_name as goodsGenericName,goods.place as goodsPlace,goods.unit_name AS unitName, goods.specification as goodsSpecification,goods.manufacturer_id as manufacturerId, goods.manufacturer as manufacturer,goods.dosage_name as dosageName, goods.chain_type as chainType, goods.approval_number as approvalNumber, varieties.tax_rate as taxRate , business.purchase_tax_rate as purchaseTaxRate, varieties.tax_rate_id as taxRateId, business.purchase_tax_rate_id as purchaseTaxRateId, business.retail_price as retailPrice,business.member_price as memberPrice, business.distr_price as distrPrice,varieties.last_pur_price as unitPrice FROM saas_goods goods LEFT JOIN saas_goods_business business on goods.id = business.goods_id INNER JOIN saas_supplier supplier on supplier.id = #{supplierId} and find_in_set(goods.management_scope_id,supplier.business_scope_id) inner join saas_supplier_business sbu on sbu.supplier_id = supplier.id and sbu.management_mode = business.management_mode LEFT JOIN saas_supplier_business_varieties varieties on goods.id = varieties.goods_id and varieties.supplier_id = #{supplierId} where goods.approve_status = 1 and goods.status = 1 and goods.valid_flag = 1 and goods.goods_nature = 0
and goods.enterprise_id = #{commonParam.parentId} and goods.owner_id in (#{commonParam.parentId},#{commonParam.enterpriseId})
and goods.enterprise_id = #{commonParam.enterpriseId} and goods.owner_id = #{commonParam.enterpriseId}
AND ( INSTR(goods.name ,#{param}) OR INSTR(goods.code ,#{param}) OR INSTR(goods.generic_name ,#{param}) OR INSTR(goods.barcode ,#{param}) OR INSTR(goods.approval_number ,#{param}) OR INSTR(goods.pinyin_code ,#{param}) )
group by goods.id
limit #{page.start},#{page.pageSize}
