mapper.select:
select

from saas_distr_in_return_out

  enterprise_id = #{enterpriseId,jdbcType=BIGINT}
  
    and base_order_id = #{returnId,jdbcType=BIGINT}
  
  and status = 41

    select
    
    from saas_distr_in_return_out
    
      enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      
        AND status IN
        
          #{item}
        
      
      
        AND distr_unit_code = #{distrUnitCode,jdbcType=VARCHAR}
      
      
        AND distr_unit_name LIKE concat('%',#{distrUnitName},'%')
      
      
        AND code = #{code,jdbcType=VARCHAR}
      
      
        AND distr_type = #{distrType,jdbcType=INTEGER}
      
      
        AND audit_man_name = #{auditManName,jdbcType=VARCHAR}
      
      
        AND out_man_name = #{outManName,jdbcType=VARCHAR}
      
      
        and out_date >= #{startTime,jdbcType=TIMESTAMP}
        and out_date < #{endTime,jdbcType=TIMESTAMP}
      
      
        and base_order_id = #{returnId,jdbcType=BIGINT}
      
    

    
      
        order by ${orderName} ${orderType}
      
      
        order by update_time desc

    SELECT
    SUM(a.real_amount_total) AS realAmountTotal,
    SUM(a.notax_real_amount_total) AS notaxRealAmountTotal,
    SUM(a.tax_amount_total) AS taxAmountTotal
    FROM (
    SELECT
    id ,code,in_return_date AS inReturnDate,distr_unit_id AS distrUnitId,
    distr_unit_code AS distrUnitCode,distr_unit_name AS distrUnitName,
    distr_type AS distrType,return_man_id AS returnManId,
    return_man_code AS returnManCode,return_man_name AS returnManName,
    real_amount_total AS realAmountTotal,notax_real_amount_total AS notaxRealAmountTotal,
    tax_amount_total AS taxAmountTotal,
    status AS  status,
    update_time AS update_time
    FROM
    saas_distr_in_return
    
      enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      AND status = 30
      
        AND distr_unit_code = #{distrUnitCode,jdbcType=VARCHAR}
      
      
        AND distr_unit_name LIKE concat('%',#{distrUnitName},'%')
      
      
        AND code = #{code,jdbcType=VARCHAR}
      
      
        AND distr_type = #{distrType,jdbcType=INTEGER}
      
      
        AND return_man_name = #{returnManName,jdbcType=VARCHAR}
      
      
        and in_return_date >= #{startTime,jdbcType=TIMESTAMP}
        and in_return_date < #{endTime,jdbcType=TIMESTAMP}
      
    

    UNION ALL

    SELECT
    sdi.id ,sdi.code,sdi.in_return_date AS inReturnDate,sdi.distr_unit_id AS distrUnitId,
    sdi.distr_unit_code AS distrUnitCode,sdi.distr_unit_name AS distrUnitName,
    sdi.distr_type AS distrType,sdi.return_man_id AS returnManId,
    sdi.return_man_code AS returnManCode,sdi.return_man_name AS returnManName,
    sdi.real_amount_total AS realAmountTotal,sdi.notax_real_amount_total AS notaxRealAmountTotal,
    sdi.tax_amount_total AS taxAmountTotal,
    sdir.status AS  status,
    sdi.update_time AS update_time
    FROM
    saas_distr_in_return sdi, saas_distr_in_return_out sdir
    
      sdi.enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      AND sdi.id = sdir.base_order_id
      AND  sdir.enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      AND sdi.status = 30 AND sdir.status = 31
      
        AND sdi.distr_unit_code = #{distrUnitCode,jdbcType=VARCHAR}
      
      
        AND sdi.distr_unit_name LIKE concat('%',#{distrUnitName},'%')
      
      
        AND sdi.code = #{code,jdbcType=VARCHAR}
      
      
        AND sdi.distr_type = #{distrType,jdbcType=INTEGER}
      
      
        AND sdi.return_man_name = #{returnManName,jdbcType=VARCHAR}
      
      
        and sdi.in_return_date >= #{startTime,jdbcType=TIMESTAMP}
        and sdi.in_return_date < #{endTime,jdbcType=TIMESTAMP}
      
    
    ) a
    
      
        order by ${orderName} ${orderType}
      
      
        order by a.update_time desc

    SELECT
    id ,code,in_return_date AS inReturnDate,distr_unit_id AS distrUnitId,
    distr_unit_code AS distrUnitCode,distr_unit_name AS distrUnitName,
    distr_type AS distrType,return_man_id AS returnManId,
    return_man_code AS returnManCode,return_man_name AS returnManName,
    real_amount_total AS realAmountTotal,notax_real_amount_total AS notaxRealAmountTotal,
    tax_amount_total AS taxAmountTotal,
    status AS  status,
    update_time AS update_time
    FROM
    saas_distr_in_return a
    
      a.enterprise_id = #{enterpriseId,jdbcType=BIGINT} and a.status in(#{waitOut,jdbcType=INTEGER},#{outIng,jdbcType=INTEGER})
      and a.id NOT in (select b.base_order_id from saas_distr_in_return_out b where b.enterprise_id = a.enterprise_id and b.base_order_id = a.id
      and b.status=#{waitAudit,jdbcType=INTEGER})
      
        AND a.distr_unit_code = #{distrUnitCode,jdbcType=VARCHAR}
      
      
        AND a.distr_unit_name LIKE concat('%',#{distrUnitName},'%')
      
      
        AND a.code = #{code,jdbcType=VARCHAR}
      
      
        AND a.distr_type = #{distrType,jdbcType=INTEGER}
      
      
        AND a.return_man_name = #{returnManName,jdbcType=VARCHAR}
      
      
        and a.in_return_date >= #{startTime,jdbcType=TIMESTAMP}
        and a.in_return_date <= #{endTime,jdbcType=TIMESTAMP}
      
    
    
      
        order by ${orderName} ${orderType}
      
      
        order by a.update_time desc

    SELECT a.* FROM (
    SELECT
    id ,code,in_return_date AS inReturnDate,distr_unit_id AS distrUnitId,
    distr_unit_code AS distrUnitCode,distr_unit_name AS distrUnitName,
    distr_type AS distrType,return_man_id AS returnManId,
    return_man_code AS returnManCode,return_man_name AS returnManName,
    real_amount_total AS realAmountTotal,notax_real_amount_total AS notaxRealAmountTotal,
    tax_amount_total AS taxAmountTotal,
    status AS  status,
    update_time AS update_time
    FROM
    saas_distr_in_return
    
      enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      AND status = #{waitOut,jdbcType=INTEGER}
      
        AND distr_unit_code = #{distrUnitCode,jdbcType=VARCHAR}
      
      
        AND distr_unit_name LIKE concat('%',#{distrUnitName},'%')
      
      
        AND code = #{code,jdbcType=VARCHAR}
      
      
        AND distr_type = #{distrType,jdbcType=INTEGER}
      
      
        AND return_man_name = #{returnManName,jdbcType=VARCHAR}
      
      
        and in_return_date >= #{startTime,jdbcType=TIMESTAMP}
        and in_return_date < #{endTime,jdbcType=TIMESTAMP}
      
    

    UNION ALL

    SELECT
    sdi.id ,sdi.code,sdi.in_return_date AS inReturnDate,sdi.distr_unit_id AS distrUnitId,
    sdi.distr_unit_code AS distrUnitCode,sdi.distr_unit_name AS distrUnitName,
    sdi.distr_type AS distrType,sdi.return_man_id AS returnManId,
    sdi.return_man_code AS returnManCode,sdi.return_man_name AS returnManName,
    sdi.real_amount_total AS realAmountTotal,sdi.notax_real_amount_total AS notaxRealAmountTotal,
    sdi.tax_amount_total AS taxAmountTotal,
    sdir.status AS  status,
    sdi.update_time AS update_time
    FROM
    saas_distr_in_return sdi, saas_distr_in_return_out sdir
    
      sdi.enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      AND sdi.id = sdir.base_order_id
      AND  sdir.enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      AND sdi.status = #{waitOut,jdbcType=INTEGER} AND sdir.status = #{auditRebut,jdbcType=INTEGER}
      
        AND sdi.distr_unit_code = #{distrUnitCode,jdbcType=VARCHAR}
      
      
        AND sdi.distr_unit_name LIKE concat('%',#{distrUnitName},'%')
      
      
        AND sdi.code = #{code,jdbcType=VARCHAR}
      
      
        AND sdi.distr_type = #{distrType,jdbcType=INTEGER}
      
      
        AND sdi.return_man_name = #{returnManName,jdbcType=VARCHAR}
      
      
        and sdi.in_return_date >= #{startTime,jdbcType=TIMESTAMP}
        and sdi.in_return_date < #{endTime,jdbcType=TIMESTAMP}
      
    
    ) a
    
      
        order by ${orderName} ${orderType}
      
      
        order by a.update_time desc

    select
      SUM(real_amount_total) AS realAmountTotal,
      SUM(notax_real_amount_total) AS notaxRealAmountTotal,
      SUM(tax_amount_total) AS taxAmountTotal
    from saas_distr_in_return_out
    
      enterprise_id = #{enterpriseId,jdbcType=BIGINT}
      AND status IN
      
        #{item}
      
      
        AND distr_unit_code = #{distrUnitCode,jdbcType=VARCHAR}
      
      
        AND distr_unit_name LIKE concat('%',#{distrUnitName},'%')
      
      
        AND code = #{code,jdbcType=VARCHAR}
      
      
        AND distr_type = #{distrType,jdbcType=INTEGER}
      
      
        AND audit_man_name = #{auditManName,jdbcType=VARCHAR}
      
      
        AND out_man_name = #{outManName,jdbcType=VARCHAR}
      
      
        and out_date >= #{startTime,jdbcType=TIMESTAMP}
        and out_date < #{endTime,jdbcType=TIMESTAMP}
      
    

    
      
        order by ${orderName} ${orderType}
      
      
        order by update_time desc

SELECT out1.id , out1.order_type as orderType, out1.code as code, out1.out_date as outDate, out1.distr_unit_id as supplierId, out1.distr_unit_code as supplierCode, out1.distr_unit_name as supplierName, out1.out_man_id as outManId, out1.out_man_code as outManCode, out1.out_man_name as outManName, out1.amount_total as amountTotal, out1.real_amount_total as realAmountTotal, out1.notax_real_amount_total as notaxRealAmountTotal, out1.tax_amount_total as taxAmountTotal, out1.status , out1.remark FROM saas_distr_in_return_out out1 WHERE out1.enterprise_id = #{enterpriseId} and out1.status in (#{status},#{partBillStatus}) and out1.outbound_unit_id = #{supplierId}
and out1.out_date >= #{startDate} and out1.out_date <= #{endDate}
order by out1.out_date desc,out1.code desc
order by out1.code ,out1.code
order by out1.out_date desc , out1.code
order by out1.out_date , out1.code desc
limit #{start},#{pageSize}

    SELECT
    count(1)
    FROM
    saas_distr_in_return_out out1
    WHERE
    out1.enterprise_id = #{enterpriseId}  and out1.status in (#{status},#{partBillStatus}) and out1.outbound_unit_id = #{supplierId}
    
       and out1.out_date >= #{startDate} 
       and out1.out_date <= #{endDate}
