SELECT shelf.id as baseShelfDtlId ,out1.code as baseOrderCode ,out1.out_date as baseOrderDate, shelf.dtl_id baseDtlId, shelf.out_id baseOrderId,out1.order_type baseOrderType, shelf.goods_id, shelf.goods_code, shelf.goods_name, shelf.lot_id, shelf.lot_number, shelf.product_date,shelf.valid_date, shelf.shelf_id, shelf.shelf_name,shelf.shelf_status_desc, shelf.unclear_quantity quantity, shelf.unit_price, shelf.line_discount, shelf.amount, shelf.whole_discount, shelf.line_discount_amount, shelf.real_price, shelf.real_amount, shelf.tax_rate_id, shelf.tax_rate, shelf.notax_real_price, shelf.notax_real_amount,shelf.tax_amount, shelf.unclear_quantity, shelf.clear_quantity, shelf.line_num, shelf.status, shelf.remark, goods.barcode, goods.generic_name, goods.dosage_id, goods.dosage_name, goods.unit_id, goods.unit_name, goods.specification, goods.manufacturer_id, goods.manufacturer, goods.place, goods.approval_number
FROM saas_distr_in_return_out_shelf shelf INNER JOIN saas_distr_in_return_out out1 ON shelf.out_id = out1.id and shelf.enterprise_id = out1.enterprise_id INNER JOIN saas_goods goods ON shelf.goods_id = goods.id WHERE shelf.enterprise_id = #{enterpriseId} and shelf.status in (#{status},#{partBillStatus}) and shelf.unclear_quantity > 0
and shelf.out_id in
#{id}
and ( INSTR(goods.name,#{param}) or INSTR(goods.code,#{param}) or INSTR(goods.generic_name,#{param}) or INSTR(goods.barcode,#{param}) or INSTR(goods.approval_number,#{param}) or INSTR(goods.pinyin_code,#{param}) )
and out1.outbound_unit_id = #{supplierId}
order by shelf.id desc
limit #{start},#{pageSize}

