mapper.update:
update saas_distr_req_plan_detail
set 
status = #{status,jdbcType=INTEGER}
where plan_id = #{planId,jdbcType=BIGINT}

mapper.select:
SELECT req.id AS id, req.plan_id AS planId, req.order_type AS orderType, req.plan_code AS planCode, req.plan_date AS planDate, req.goods_id AS goodsId, req.goods_code AS goodsCode, req.barcode AS barcode, req.goods_name AS goodsName, req.goods_generic_name AS goodsGenericName, req.dosage_id AS dosageId, req.dosage_name AS dosageName, req.unit_id AS unitId, req.unit_name AS unitName, req.goods_specification AS goodsSpecification, req.manufacturer_id AS manufacturerId, req.manufacturer AS manufacturer, req.goods_place AS goodsPlace, req.approval_number AS approvalNumber, req.quantity AS requestQuantity, req.line_num AS lineNum, req.status AS status, req.remark AS remark, se.id AS requestUnitId, se.code AS requestUnitCode, se.name AS requestUnitName FROM saas_distr_req_plan_detail req LEFT JOIN saas_enterprise se ON req.enterprise_id = se.id WHERE req.plan_id IN
#{item}
ORDER BY ${sort}

SELECT
count(1)
FROM saas_distr_req_plan_detail drpd
LEFT JOIN saas_distr_req_plan drp ON drp.id = drpd.plan_id
where plan_id = #{planId}

  and drpd.plan_date between #{startTimes} and #{endTimes}

SELECT drpd.plan_id AS planId, drpd.id, drpd.goods_code AS goodsCode, drpd.goods_generic_name AS goodsGenericName, drpd.dosage_name AS dosageName, drpd.goods_specification AS goodsSpecification, drpd.unit_name AS unitName, drpd.manufacturer, drpd.quantity FROM saas_distr_req_plan_detail drpd LEFT JOIN saas_distr_req_plan drp ON drp.id = drpd.plan_id where plan_id = #{planId}
and drpd.plan_date between #{startTimes} and #{endTimes}
limit #{start},#{pageSize}
