mapper.select:
select a.id, a.out_date, a.code, a.request_unit_id, a.request_unit_code, a.request_unit_name, a.distr_type, a.send_man_name, a.out_man_name, a.checker_name, a.real_amount_total, a.notax_real_amount_total, a.tax_amount_total, a.`status` from saas_distr_out a
order by ${sort}
limit #{pageNo},#{pageSize}

select b.goods_id, a.out_date, a.code as discode, a.request_unit_code, a.request_unit_name, a.base_order_code, a.out_man_name, a.checker_name, a.flow_code, a.distr_type, f.lot_number, f.product_date, f.valid_date, f.shelf_name, f.shelf_status_desc, f.quantity, f.unit_price, f.line_discount, f.amount, f.whole_discount, f.line_discount_amount, f.real_price, f.real_amount, f.tax_rate, f.notax_real_price, f.notax_real_amount, f.tax_amount, a.`status`, c.code, c.barcode, c.name, c.generic_name, c.dosage_id, c.dosage_name, c.unit_id, c.unit_name, c.specification, c.manufacturer, c.manufacturer_id, c.place, c.approval_number, c.business_variety, e.name as category_name, c.goods_attribute, c.domestic_import, c.management_scope_name, c.special_drug, c.in_charge_drug, c.limit_quantity, c.storage_temp, c.storage_condition_name, c.quality_period, c.quality_period_unit from saas_distr_out a INNER join saas_distr_out_detail b on b.out_id=a.id INNER join saas_distr_out_shelf f on f.dtl_id=b.id INNER join saas_goods c on c.id=b.goods_id INNER join saas_enterprise d on d.id=a.enterprise_id INNER join saas_goods_category e on e.id=c.category_id
order by ${sort}
limit #{pageNo},#{pageSize}

select

from saas_distr_out
where base_order_id = #{baseOrderId}
and enterprise_id = #{enterpriseId}

SELECT
sum(cou)
FROM
(

  SELECT
  count(s.id) cou
  FROM saas_distr_out s
  LEFT JOIN saas_enterprise e ON e.id=s.request_unit_id
  WHERE s.enterprise_id=#{enterpriseId}
  AND s.distr_type = #{distrType}
  AND e.chain_type = #{chainType}
  AND e.parent_id =#{enterpriseId}
  AND s.status IN
  
    #{status}
  
  AND s.out_date <= #{endDate}
  AND s.out_date >= #{startDate}
  
    AND s.request_unit_name like concat('%',#{invoiceName},'%')
  
  
    AND s.request_unit_code =#{code}
  


  UNION ALL


  SELECT count(s.id) cou
  FROM saas_distr_return_in s
  LEFT JOIN saas_enterprise e ON e.id=s.request_unit_id
  WHERE s.enterprise_id=#{enterpriseId}
  AND s.distr_type = #{distrType}
  AND e.chain_type = #{chainType}
  AND e.parent_id =#{enterpriseId}
  AND s.status IN
  
    #{status}
  
  AND s.return_in_date <= #{endDate}
  AND s.return_in_date >= #{startDate}
  
    AND s.request_unit_name like concat('%',#{invoiceName},'%')
  
  
    AND s.request_unit_code =#{code}
  
) t


  SELECT
  s.request_unit_code AS invoiceCode,
  s.request_unit_name AS invoiceName,
  date(s.out_date) AS orderDate,
  s.`code` AS orderCode,
  '配货出库' AS orderType,
  s.real_amount_total AS orderAmount,
  round(d.clearAmount,2) AS clearAmount,
  round(d.unClearAmount,2) AS unClearAmount
  FROM saas_distr_out s
  LEFT JOIN saas_enterprise e ON e.id=s.request_unit_id
  LEFT JOIN
  (SELECT
  out_id AS id,
  sum(real_price * unclear_quantity) AS unClearAmount,
  sum(real_price * clear_quantity) AS clearAmount
  FROM saas_distr_out_detail
  WHERE  enterprise_id=#{enterpriseId}
  AND out_date <= #{endDate}
  AND out_date >= #{startDate}
  GROUP BY out_id) d
  ON d.id = s.id
  WHERE s.enterprise_id=#{enterpriseId}
  AND s.distr_type = #{distrType}
  AND e.chain_type = #{chainType}
  AND e.parent_id =#{enterpriseId}
  AND s.status IN
  
    #{status}
  
  AND s.out_date <= #{endDate}
  AND s.out_date >= #{startDate}
  
    AND s.request_unit_name LIKE concat('%',#{invoiceName},'%')
  
  
    AND s.request_unit_code =#{code}
  


  UNION ALL


  SELECT
  o.request_unit_code AS invoiceCode,
  o.request_unit_name AS invoiceName,
  date(o.return_in_date) AS orderDate,
  o.code AS orderCode,
  '配后退回入库' AS orderType,
  -o.real_amount_total AS orderAmount,
  round(-d.clearAmount,2) AS clearAmount,
  round(-d.unClearAmount,2) AS unClearAmount
  FROM saas_distr_return_in o
  LEFT JOIN saas_enterprise e ON e.id=o.request_unit_id
  LEFT JOIN
  (SELECT
  return_in_id AS id,
  sum(real_price * unclear_quantity) AS unClearAmount,
  sum(real_price * clear_quantity) AS clearAmount
  FROM
  saas_distr_return_in_detail
  WHERE enterprise_id=#{enterpriseId}
  AND return_in_date <= #{endDate}
  AND return_in_date >= #{startDate}
  GROUP BY return_in_id) d ON d.id = o.id
  WHERE o.enterprise_id=#{enterpriseId}
  AND o.distr_type = #{distrType}
  AND e.chain_type = #{chainType}
  AND e.parent_id =#{enterpriseId}
  AND o.status IN
  
      #{status}
  
  AND o.return_in_date <= #{endDate}
  AND o.return_in_date >= #{startDate}
  
    AND o.request_unit_name LIKE concat('%',#{invoiceName},'%')
  
  
    AND o.request_unit_code =#{code}
  

ORDER BY invoiceCode ASC,orderDate ASC,orderCode ASC

  limit #{start},#{pageSize}
