mapper.select:
    SELECT
    id, table_name, goods_id, reason, modifier_id, modifier_code,
    modifier_name, update_time,column_en_name, column_ch_name, old_content, new_content
    FROM saas_goods_modify_record mr
    WHERE 1=1 AND
     mr.enterprise_id = #{enterpriseId}
    AND mr.parent_id = #{parentId}
    and mr.goods_id = #{goodsId}
    order by mr.id desc
