SELECT
k.orderShelfId, k.code, k.orderType, k.goodsId, k.goodsName, k.lotNumber, k.productDate, k.orderDate, k.validUntil, k.quantity, k.clearQuantity, k.unclearQuantity, k.unitPrice, k.taxRateId, k.taxRate, k.notaxRealPrice, k.managementMode, k.verificationQuantity, k.unverificationQuantity
FROM ( SELECT piss.id AS orderShelfId, pis.code, pis.order_type AS orderType, piss.goods_id AS goodsId, piss.goods_name AS goodsName, piss.lot_number AS lotNumber, piss.product_date AS productDate, pis.in_storage_date AS orderDate, piss.valid_date AS validUntil, piss.quantity, piss.clear_quantity AS clearQuantity, piss.unclear_quantity AS unclearQuantity, piss.real_price AS unitPrice, piss.tax_rate_id AS taxRateId, piss.tax_rate AS taxRate, piss.notax_real_price AS notaxRealPrice, goods.management_mode AS managementMode, piss.verification_quantity AS verificationQuantity, piss.unverification_quantity AS unverificationQuantity FROM saas_purchase_in_storage pis, saas_purchase_in_storage_detail pisd , saas_purchase_in_storage_shelf piss, saas_goods_business goods WHERE pis.id = pisd.in_storage_id and goods.management_mode = 0 and piss.unclear_quantity > 0 and goods.goods_id = pisd.goods_id and pis.supplier_id = #{supplierId,jdbcType=BIGINT} AND pis.id = piss.in_storage_id and pisd.id = piss.in_storage_dtl_id and pisd.enterprise_id=#{enterpriseId,jdbcType=BIGINT} and piss.enterprise_id=#{enterpriseId,jdbcType=BIGINT} and pis.enterprise_id=#{enterpriseId,jdbcType=BIGINT} AND pisd.goods_id = #{goodsId,jdbcType=BIGINT} AND piss.goods_id = #{goodsId,jdbcType=BIGINT} AND piss.status in
#{item}
and pis.in_storage_date >= #{startDate,jdbcType=TIMESTAMP} and pis.in_storage_date <= #{endDate,jdbcType=TIMESTAMP}
union
SELECT piss.id AS orderShelfId, pis.code, pis.order_type AS orderType, piss.goods_id AS goodsId, piss.goods_name AS goodsName, piss.lot_number AS lotNumber, piss.product_date AS productDate, pis.in_storage_date AS orderDate, piss.valid_date AS validUntil, piss.quantity, piss.clear_quantity AS clearQuantity, piss.unclear_quantity AS unclearQuantity, piss.real_price AS unitPrice, piss.tax_rate_id AS taxRateId, piss.tax_rate AS taxRate, piss.notax_real_price AS notaxRealPrice, goods.management_mode AS managementMode, piss.verification_quantity AS verificationQuantity, piss.unverification_quantity AS unverificationQuantity FROM saas_purchase_in_storage pis, saas_purchase_in_storage_detail pisd , saas_purchase_in_storage_shelf piss, saas_goods_business goods WHERE pis.id = pisd.in_storage_id and goods.management_mode = 1 and piss.verification_quantity > 0 and piss.verification_quantity > piss.clear_quantity and goods.goods_id = pisd.goods_id and pis.supplier_id = #{supplierId,jdbcType=BIGINT} AND pis.id = piss.in_storage_id and pisd.id = piss.in_storage_dtl_id and pisd.enterprise_id=#{enterpriseId,jdbcType=BIGINT} and piss.enterprise_id=#{enterpriseId,jdbcType=BIGINT} and pis.enterprise_id=#{enterpriseId,jdbcType=BIGINT} AND pisd.goods_id = #{goodsId,jdbcType=BIGINT} AND piss.goods_id = #{goodsId,jdbcType=BIGINT} AND piss.status in
#{item}
and pis.in_storage_date >= #{startDate,jdbcType=TIMESTAMP} and pis.in_storage_date <= #{endDate,jdbcType=TIMESTAMP}
) k order by k.orderShelfId asc

