mapper.select:
    select
    
    from saas_purchase_order_detail dtl,
    saas_purchase_order doc,
    saas_goods goods
    where goods.id = dtl.goods_id
    and dtl.order_id = doc.id
    and dtl.enterprise_id = #{enterpriseId}
    
      and (
      goods.code like concat('%', #{orderPageVO.param},'%')
      or
      goods.barcode like concat('%', #{orderPageVO.param},'%')
      or
      goods.pinyin_code like concat('%', #{orderPageVO.param},'%')
      or
      goods.name like concat('%', #{orderPageVO.param},'%')
      or
      goods.generic_name like concat('%', #{orderPageVO.param},'%')
      or
      goods.approval_number like concat('%', #{orderPageVO.param},'%')
      )
    
    
      and goods.business_variety = #{orderPageVO.businessVariety}
    
    
      and doc.supplier_code = #{orderPageVO.supplierCode}
    
    
      and doc.supplier_name like concat('%', #{orderPageVO.supplierName},'%')
    
    
      and doc.order_date between #{orderPageVO.startDate} and #{orderPageVO.endDate}
    
    
      and doc.code = #{orderPageVO.code}
    
    
      and doc.purchaser_name like concat('%',#{orderPageVO.purchaserName},'%')
    
    
      and doc.arrival_time = #{orderPageVO.arrivalTime}
    
    
      and dtl.status = #{orderPageVO.status}
    
    
      and goods.category_id = #{orderPageVO.categoryId}
    
    
      and goods.domestic_import = #{orderPageVO.domesticImport}
    
    
      and goods.storage_temp = #{orderPageVO.storageTemp}
    
    
      and goods.storage_condition_name = #{orderPageVO.storageConditionName}
    
    
      and goods.goods_attribute = #{orderPageVO.goodsAttribute}
      
        and goods.prescription_drug = #{orderPageVO.prescriptionDrug}
        
          and goods.otc_type = #{orderPageVO.otcType}
        
      
    
    
      and goods.special_drug = #{orderPageVO.specialDrug}
      
        and goods.spirit_drug_type = #{orderPageVO.spiritDrugType}
      
    
    
    and goods.in_charge_drug = #{orderPageVO.inChargeDrug}
      
        and goods.formulation_type = #{orderPageVO.formulationType}
      
    
    
      and goods.brand like concat('%', #{orderPageVO.brand},'%')
    
    
      and goods.registered_trademark like concat('%', #{orderPageVO.registeredTrademark},'%')
    

    
      order by ${orderPageVO.orderName} ${orderPageVO.orderType}

