mapper.update:
update saas_purchase_order_detail
set 
  status = #{status,jdbcType=INTEGER}
where order_id = #{orderId,jdbcType=BIGINT}

update saas_purchase_order_detail
set
  status = #{status,jdbcType=INTEGER}
