SELECT g.business_variety as businessVariety, e.code as storeCode, e.name as storeName, s.goods_code as code, g.generic_name as genericName, g.dosage_name as dosageName, g.specification as specification, g.manufacturer as manufacturer, g.unit_name as unitName, g.place as place, s.lot_num as lotNum, ln.product_date as productDate, ln.valid_until as validUntil, s.shelf_id as shelfId, s.shelf_name as shelfName, sum(s.quantity) as stockQuantity, sum(s.usable_quantity) as usableQuantity, gs.maintenance_type as maintanceType, case when 0 = gs.maintenance_cycle_unit then (ifnull(gs.maintenance_cycle, 0)) when 1 = gs.maintenance_cycle_unit then (ifnull(gs.maintenance_cycle, 0) * 30) when 2 = gs.maintenance_cycle_unit then (ifnull(gs.maintenance_cycle, 0) * 365) END AS maintanceCycle, s.last_maintain_time as lastMaintanceDate,
case when 0 = gs.near_effect_period_unit then (ifnull(near_effect_period, 0)) when 1 = gs.near_effect_period_unit then (ifnull(near_effect_period, 0) * 30) when 2 = gs.near_effect_period_unit then (ifnull(near_effect_period, 0) * 365) END AS nearPeriodCycle FROM saas_stock s LEFT JOIN saas_goods g ON s.goods_id = g.id left join saas_lot_number ln on s.lot_id = ln.id left join saas_goods_storage gs on s.goods_id = gs.goods_id left join saas_goods_business gb on s.goods_id = gb.goods_id LEFT JOIN saas_enterprise e on s.enterprise_id = e.id where s.quantity > 0
and (s.enterprise_id = #{parentId} or s.parent_id = #{parentId})
and s.enterprise_id = #{vo.storeId}
and e.name like '%${vo.storeName}%'
and g.business_variety = #{vo.businessVariety}
and g.goods_attribute = #{vo.goodsAttribute}
and g.prescription_drug = #{vo.prescriptionDrug}
and g.category_id = #{vo.categoryId}
and g.goods_nature = #{vo.goodsNature}
and g.domestic_import = #{vo.domesticImport}
and g.storage_temp = #{vo.storageTemp}
and gs.maintenance_type = #{vo.maintenanceType}
and g.medical_insurance = #{vo.medicalInsurance}
and g.management_scope_id = #{vo.managementScopeId}
and g.special_drug = #{vo.specialDrug}
and g.in_charge_drug = #{vo.inChargeDrug}
and g.configuration_flag = #{vo.configurationFlag}
and gb.management_mode = #{vo.managementMode}
and g.registered_trademark like '%${vo.registeredTrademark}%'
and g.brand like '%${vo.brand}%'
and g.grade like '%${vo.grade}%'
and s.shelf_id in (select id from saas_warehouse_shelf where warehouse_area_id = #{vo.areaId})
and s.shelf_id in (select id from saas_warehouse_shelf where cargo_area_id = #{vo.cargoAreaId})
and s.shelf_id = #{vo.shelfId}
and s.lot_num like '%${vo.lotNum}%'
and ( INSTR(s.goods_code, #{vo.key}) or INSTR(g.barcode, #{vo.key}) or INSTR(g.pinyin_code, #{vo.key}) or INSTR(s.goods_name, #{vo.key}) or INSTR(g.generic_name, #{vo.key}) or INSTR(g.approval_number, #{vo.key}) )
group by s.enterprise_id, s.goods_id, s.lot_id, s.shelf_id
order by ${order} ${sort}
limit #{start}, #{pageSize}

