mapper.select:
select count(1) from saas_goods_clear where enterprise_id = #{enterpriseId}
and clear_date between #{startTimes} and #{endTimes}
AND ( code = #{code})
AND INSTR( clear_man_name , #{clearManName})
AND INSTR( audit_man_name , #{auditManName})

select id, code, date_format(clear_date, '%Y-%m-%d') AS clearDate, clear_man_name AS clearManName, audit_man_name AS auditManName, remark from saas_goods_clear where enterprise_id = #{enterpriseId}
and clear_date between #{startTimes} and #{endTimes}
AND ( code = #{code})
AND INSTR( clear_man_name ,#{clearManName})
AND INSTR( audit_man_name , #{auditManName})
ORDER BY ${orderName} ${orderType}
limit #{start},#{pageSize}

/** 搜寻中药清斗报表（总部） */ SELECT s.id as id, u.code as enterpriseCode, u.name as enterpriseName, c.clear_date as clearDate, c.code as code, c.clear_man_name as clearManName, c.audit_man_name as auditManName, c.flow_code as flowCode, d.goods_id as goodsId, d.goods_code as goodsCode, d.barcode as barcode, d.goods_name as goodsName, d.goods_generic_name as goodsGenericName, d.dosage_id as dosageId, d.dosage_name as dosageName, d.goods_specification as goodsSpecification, d.unit_id as unitId, d.unit_name as unitName, d.manufacturer_id as manufacturerId, d.manufacturer as manufacturer, d.goods_place as goodsPlace, d.approval_number as approvalNumber, s.lot_number as lotNum, s.product_date as productDate, s.valid_date as validDate, s.shelf_id as shelfId, s.shelf_name as shelfName, s.shelf_status_desc as shelfStatusDesc, s.quantity as quantity, s.remark as remark, c.status as status, g.business_variety as businessVariety, g.category_id as categoryId, ( SELECT cate.name FROM saas_goods_category cate WHERE cate.id = g.category_id ) AS categoryName, g.goods_attribute as goodsAttribute, g.prescription_drug as prescriptionDrug, g.otc_type as otcType, g.cosmetics as cosmetics, g.domestic_import as domesticImport, g.management_scope_id as managementScopeId, g.management_scope_name as managementScopeName, g.special_drug as specialDrug, g.spirit_drug_type as spiritDrugType, g.formulation_type as formulationType, g.in_charge_drug as inChargeDrug, g.limit_quantity as limitQuantity, g.storage_temp as storageTemp, g.storage_condition_name as storageConditionName, g.quality_period as qualityPeriod, g.quality_period_unit as qualityPeriodUnit FROM saas_goods_clear_shelf s LEFT JOIN saas_goods_clear_detail d ON s.dtl_id = d.id LEFT JOIN saas_goods_clear c ON s.clear_id = c.id LEFT JOIN saas_enterprise u ON u.id = s.enterprise_id LEFT JOIN saas_goods g ON g.id = s.goods_id WHERE (u.id = #{enterpriseId} or u.parent_id = #{enterpriseId})
and g.business_variety = #{businessVariety}
AND ( INSTR(d.goods_code ,#{param}) OR INSTR(d.barcode ,#{param}) OR INSTR(d.goods_name ,#{param}) OR INSTR(d.goods_generic_name ,#{param}) OR INSTR(d.approval_number ,#{param}) )
and u.chain_type = #{chainType}
and INSTR(u.code ,#{enterpriseCode})
and INSTR(u.name , #{enterpriseName})
/** 起始时间----终止时间 */
and c.clear_date >= #{startTime}
and c.clear_date <= #{endTime}
and INSTR(c.code , #{code})
and INSTR(c.clear_man_name , #{clearManName})
and INSTR(c.audit_man_name , #{auditManName})
and INSTR(c.flow_code , #{flowCode})
and c.status = #{status}
and g.category_id = #{categoryId}
and g.domestic_import = #{domesticImport}
and g.storage_temp = #{storageTemp}
and INSTR(g.storage_condition_name , #{storageConditionName})
and INSTR(g.registered_trademark , #{registeredTrademark})
and INSTR(g.brand , #{brand})
and g.goods_attribute = #{goodsAttribute}
and g.prescription_drug = #{prescriptionDrug}
and g.otc_type = #{otcType}
and g.management_scope_id = #{managementScopeId}
and g.special_drug = #{specialDrug}
and g.in_charge_drug = #{inChargeDrug}
and g.spirit_drug_type = #{spiritDrugType}
and g.formulation_type = #{formulationType}
order by c.${order} ${sort}
order by c.id desc
limit #{start},#{pageSize}

/** 搜寻中药清斗报表（分部） */ SELECT s.id as id, c.clear_date as clearDate, c.code as code, c.clear_man_name as clearManName, c.audit_man_name as auditManName, c.flow_code as flowCode, d.goods_id as goodsId, d.goods_code as goodsCode, d.barcode as barcode, d.goods_name as goodsName, d.goods_generic_name as goodsGenericName, d.dosage_id as dosageId, d.dosage_name as dosageName, d.goods_specification as goodsSpecification, d.unit_id as unitId, d.unit_name as unitName, d.manufacturer_id as manufacturerId, d.manufacturer as manufacturer, d.goods_place as goodsPlace, d.approval_number as approvalNumber, s.lot_number as lotNum, s.product_date as productDate, s.valid_date as validDate, s.shelf_id as shelfId, s.shelf_name as shelfName, s.shelf_status_desc as shelfStatusDesc, s.quantity as quantity, s.remark as remark, c.status as status, g.business_variety as businessVariety, g.category_id as categoryId, ( SELECT cate.name FROM saas_goods_category cate WHERE cate.id = g.category_id ) AS categoryName, g.goods_attribute as goodsAttribute, g.prescription_drug as prescriptionDrug, g.otc_type as otcType, g.cosmetics as cosmetics, g.domestic_import as domesticImport, g.management_scope_id as managementScopeId, g.management_scope_name as managementScopeName, g.special_drug as specialDrug, g.spirit_drug_type as spiritDrugType, g.formulation_type as formulationType, g.in_charge_drug as inChargeDrug, g.limit_quantity as limitQuantity, g.storage_temp as storageTemp, g.storage_condition_name as storageConditionName, g.quality_period as qualityPeriod, g.quality_period_unit as qualityPeriodUnit FROM saas_goods_clear_shelf s LEFT JOIN saas_goods_clear_detail d ON s.dtl_id = d.id LEFT JOIN saas_goods_clear c ON s.clear_id = c.id LEFT JOIN saas_goods g ON g.id = s.goods_id WHERE s.enterprise_id = #{enterpriseId}
and g.business_variety = #{businessVariety}
AND ( INSTR(d.goods_code ,#{param}) OR INSTR(d.barcode ,#{param}) OR INSTR(d.goods_name ,#{param}) OR INSTR(d.goods_generic_name ,#{param}) OR INSTR(d.approval_number ,#{param}) )
/** 起始时间----终止时间 */
and c.clear_date >= #{startTime}
and c.clear_date <= #{endTime}
and INSTR(c.code , #{code})
and INSTR(c.clear_man_name ,#{clearManName})
and INSTR(c.audit_man_name , #{auditManName})
and INSTR(c.flow_code , #{flowCode})
and c.status = #{status}
and g.category_id = #{categoryId}
and g.domestic_import = #{domesticImport}
and g.storage_temp = #{storageTemp}
and INSTR(g.storage_condition_name , #{storageConditionName})
and INSTR(g.registered_trademark , #{registeredTrademark})
and INSTR(g.brand , #{brand})
and g.goods_attribute = #{goodsAttribute}
and g.prescription_drug = #{prescriptionDrug}
and g.otc_type = #{otcType}
and g.management_scope_id = #{managementScopeId}
and g.special_drug = #{specialDrug}
and g.in_charge_drug = #{inChargeDrug}
and g.spirit_drug_type = #{spiritDrugType}
and g.formulation_type = #{formulationType}
order by c.${order} ${sort}
order by c.id desc
limit #{start},#{pageSize}

    SELECT
    sum(s.quantity)
    FROM saas_goods_clear_shelf s
    LEFT JOIN saas_goods_clear_detail d
    ON s.dtl_id = d.id
    LEFT JOIN saas_goods_clear c
    ON s.clear_id = c.id
    LEFT JOIN saas_enterprise u
    ON u.id = s.enterprise_id
    LEFT JOIN saas_goods g
    ON g.id = s.goods_id
    WHERE (u.id = #{enterpriseId} or u.parent_id = #{enterpriseId})
    
      and g.business_variety = #{businessVariety}
    
    
      AND (
      INSTR(d.goods_code ,#{param})
      OR INSTR(d.barcode ,#{param})
      OR INSTR(d.goods_name ,#{param})
      OR INSTR(d.goods_generic_name ,#{param})
      OR INSTR(d.approval_number ,#{param})
      )
    
    
      and u.chain_type = #{chainType}
    
    
      and INSTR(u.code , #{enterpriseCode})
    
    
      and INSTR(u.name , #{enterpriseName})
    
    /**
    起始时间----终止时间
    */
    
      and c.clear_date >= #{startTime}
    
    
      and c.clear_date <= #{endTime}
    
    
      and INSTR(c.code , #{code})
    
    
      and INSTR(c.clear_man_name , #{clearManName})
    
    
      and INSTR(c.audit_man_name , #{auditManName})
    
    
      and INSTR(c.flow_code , #{flowCode})
    
    
      and c.status = #{status}
    
    
      and g.category_id = #{categoryId}
    
    
      and g.domestic_import = #{domesticImport}
    
    
      and g.storage_temp = #{storageTemp}
    
    
      and INSTR(g.storage_condition_name , #{storageConditionName})
    
    
      and INSTR(g.registered_trademark , #{registeredTrademark})
    
    
      and INSTR(g.brand , #{brand})
    
    
      and g.goods_attribute = #{goodsAttribute}
    
    
      and g.prescription_drug = #{prescriptionDrug}
    
    
      and g.otc_type = #{otcType}
    
    
      and g.management_scope_id = #{managementScopeId}
    
    
      and g.special_drug = #{specialDrug}
    
    
      and g.in_charge_drug = #{inChargeDrug}
    
    
      and g.spirit_drug_type = #{spiritDrugType}
    
    
      and g.formulation_type = #{formulationType}
    
    
      
        order by c.${order} ${sort}
      
      
        order by c.id desc

