mapper.select:
    SELECT

    

    FROM
    saas_inventory inventory
    WHERE 1=1
    AND inventory.enterprise_id = #{enterpriseId}
    
      and inventory.code = #{paramForListVO.code}
    
    
      and
      inventory.creater_name like concat('%',#{paramForListVO.createrName},'%')
    

    
      and
      inventory.handle_man_name like concat('%',#{paramForListVO.handleManName},'%')
    
    
      and
      inventory.inv_type = #{paramForListVO.invType}
    
    
      and
      inventory.inv_range = #{paramForListVO.invRange}
    
    
      and
      inventory.status = #{paramForListVO.status}
    
    
      and inventory.create_time >= #{paramForListVO.startDate}
      and inventory.create_time <= #{paramForListVO.endDate}
    
    
      order by inventory.id desc,inventory.create_time desc,inventory.code desc
    
    
      order by inventory.id desc,inventory.create_time ,inventory.code
    
    
      order by inventory.id desc,inventory.create_time desc,inventory.code
    
    
      order by inventory.id desc,inventory.create_time ,inventory.code desc

SELECT
FROM saas_inventory inventory WHERE 1=1
AND (inventory.status = 2 OR inventory.status = 3)
AND (inventory.status = 2)
AND inventory.enterprise_id = #{enterpriseId}
and inventory.code = #{paramForListVO.code}
and inventory.handle_man_name like concat('%',#{paramForListVO.handleManName},'%')
and inventory.handle_date >= #{paramForListVO.startDate} and inventory.handle_date <= #{paramForListVO.endDate}
order by inventory.id desc,inventory.handle_date desc,inventory.code desc
order by inventory.id desc,inventory.handle_date ,inventory.code
order by inventory.id desc,inventory.handle_date desc,inventory.code
order by inventory.id desc,inventory.handle_date ,inventory.code desc
limit #{paramForListVO.start},#{paramForListVO.pageSize}

