mapper.select:
select

from saas_other_in_detail
where enterprise_id = #{enterpriseId}
and in_id = #{id}

select

from saas_other_in_detail
where (enterprise_id = #{enterpriseId} or parent_id = #{enterpriseId})
and goods_id = #{goodsId}
AND lot_number = #{lotNumber}

mapper.update:
UPDATE saas_other_in_detail
SET status = #{approvalStatus}
where in_id = #{id}
