mapper.select:
      select
      
      from saas_other_in
      where enterprise_id = #{enterpriseId}
      
        and in_date between #{startTime} and #{endTime}
      
      
        and INSTR(code,#{code})
      
      
        and INSTR(in_man_name,#{inManName})
      
      
        and in_type = #{inType}
      

      
        and  (status = 0 or status = -1)
      
      
        and  status = #{approveStatus,jdbcType=INTEGER}
      

      
        
          order by ${order} ${sort}
        
        
          order by id desc
        
      
      
        limit #{start},#{pageSize}

SELECT goods.id as goodsId, goods.code AS goodsCode, goods.name as goodsName, goods.barcode as barcode, goods.generic_name AS goodsGenericName, goods.dosage_id as dosageId, goods.dosage_name as dosageName, goods.specification AS goodsSpecification, goods.unit_id as unitId, goods.unit_name as unitName, goods.manufacturer_id AS manufacturerId, goods.manufacturer AS manufacturer, goods.place as goodsPlace, goods.approval_number as approvalNumber, safe.default_shelf_id as shelfId, safe.default_shelf_name as shelfName, business.purchase_tax_rate_id as taxRateId, business.purchase_tax_rate as taxRate, storage.near_effect_period AS nearEffectPeriod, storage.near_effect_period_unit AS nearEffectPeriodUnit
from saas_goods goods, saas_goods_business business,saas_goods_storage storage,saas_safety_stock safe
where
goods.enterprise_id = #{enterpriseId} and goods.owner_id in ( #{divisionId},#{enterpriseId}) and safe.enterprise_id = #{divisionId}
goods.enterprise_id = #{enterpriseId} and goods.owner_id = #{enterpriseId} and safe.enterprise_id = #{selfShopId}
goods.enterprise_id = #{enterpriseId} and goods.owner_id = #{enterpriseId} and safe.enterprise_id = #{enterpriseId}
and business.goods_id = goods.id and goods.id = storage.goods_id and safe.goods_id = goods.id and goods.status = '1' and goods.valid_flag = '1' and goods.approve_status = '1'
and ( INSTR(goods.name,#{param}) or INSTR(goods.code,#{param}) or INSTR(goods.pinyin_code,#{param}) or INSTR(goods.approval_number,#{param}) or INSTR(goods.barcode,#{param}) or INSTR(goods.generic_name,#{param}) )
limit 100

/** 搜寻其他入库报表（总部） */ SELECT d.id as id, u.code as enterpriseCode, u.name as enterpriseName, d.in_date as inDate, d.in_code as code, m.in_man_name as inManName, m.in_type as inType, m.src_unit_name as srcUnitName, m.flow_code as flowCode, d.goods_id as goodsId, d.goods_code as goodsCode, d.barcode as barcode, d.goods_name as goodsName, d.goods_generic_name as goodsGenericName, d.dosage_id as dosageId, d.dosage_name as dosageName, d.goods_specification as goodsSpecification, d.unit_id as unitId, d.unit_name as unitName, d.manufacturer_id as manufacturerId, d.manufacturer as manufacturer, d.goods_place as goodsPlace, d.approval_number as approvalNumber, d.lot_number as lotNum, d.product_date as productDate, d.valid_date as validDate, d.shelf_id as shelfId, d.shelf_name as shelfName, d.shelf_status_desc as shelfStatusDesc, d.quantity as quantity, d.unit_price as unitPrice, d.amount as amount, d.tax_rate_id as taxRateId, d.tax_rate as taxRate, d.notax_price as notaxPrice, d.notax_amount as notaxAmount, d.tax_amount as taxAmount, d.remark as remark, m.status as status, g.business_variety as businessVariety, g.category_id as categoryId, ( SELECT cate.name FROM saas_goods_category cate WHERE cate.id = g.category_id ) AS categoryName, g.goods_attribute as goodsAttribute, g.prescription_drug as prescriptionDrug, g.otc_type as otcType, g.cosmetics as cosmetics, g.domestic_import as domesticImport, g.management_scope_id as managementScopeId, g.management_scope_name as managementScopeName, g.special_drug as specialDrug, g.spirit_drug_type as spiritDrugType, g.formulation_type as formulationType, g.in_charge_drug as inChargeDrug, g.limit_quantity as limitQuantity, g.storage_temp as storageTemp, g.storage_condition_name as storageConditionName, g.quality_period as qualityPeriod, g.quality_period_unit as qualityPeriodUnit FROM saas_other_in_detail d LEFT JOIN saas_other_in m ON d.in_id = m.id LEFT JOIN saas_enterprise u ON u.id = d.enterprise_id LEFT JOIN saas_goods g ON g.id = d.goods_id WHERE (u.id = #{enterpriseId} or u.parent_id = #{enterpriseId})
and g.business_variety = #{businessVariety}
and ( INSTR(d.goods_name,#{param}) or INSTR(d.goods_code,#{param}) or INSTR(d.approval_number,#{param}) or INSTR(d.barcode,#{param}) or INSTR(d.goods_generic_name,#{param}))
and u.chain_type = #{chainType}
and INSTR(u.code,#{enterpriseCode})
and INSTR(u.name,#{enterpriseName})
/** 起始时间----终止时间 */
and d.in_date >= #{startTime}
and d.in_date <= #{endTime}
and INSTR(d.in_code,#{code})
and INSTR(m.in_man_name,#{inManName})
and m.in_type = #{inType}
and m.src_unit_type = #{srcUnitType}
and m.src_unit_id = #{srcUnitId}
and g.category_id = #{categoryId}
and g.domestic_import = #{domesticImport}
and g.storage_temp = #{storageTemp}
and INSTR(g.storage_condition_name,#{storageConditionName})
and INSTR(g.registered_trademark, #{registeredTrademark})
and INSTR(g.brand,#{brand})
and g.goods_attribute = #{goodsAttribute}
and g.prescription_drug = #{prescriptionDrug}
and g.otc_type = #{otcType}
and g.management_scope_id = #{managementScopeId}
and g.special_drug = #{specialDrug}
and g.in_charge_drug = #{inChargeDrug}
and g.spirit_drug_type = #{spiritDrugType}
and g.formulation_type = #{formulationType}
order by d.${order} ${sort}
order by d.id desc
limit #{start},#{pageSize}

