mapper.select:
select

from saas_shelf_move
where enterprise_id = #{enterpriseId}

  and move_date between #{startTime} and #{endTime}


  and INSTR(code,#{code})


  and INSTR(move_man_name,#{moveManName})


  and INSTR(receiver_name,#{receiverName})


  
    order by ${order} ${sort}
  
  
    order by id desc
  


  limit #{start},#{pageSize}

/** 新增时查出的商品列表--库存表中有库存 */ SELECT goods.id as goodsId, goods.code AS goodsCode, goods.name as goodsName, goods.barcode as barcode, goods.generic_name AS goodsGenericName, goods.dosage_id as dosageId, goods.dosage_name as dosageName, goods.specification AS goodsSpecification, goods.unit_id as unitId, goods.unit_name as unitName, goods.manufacturer_id AS manufacturerId, goods.manufacturer AS manufacturer, goods.place as goodsPlace, goods.approval_number as approvalNumber,
lot.lot_num as lotNumber, lot.id as lotId, lot.product_date as productDate, lot.valid_until as validDate,
ws.id as srcShelfId, ws.name as srcShelfName, st.shelf_status_desc as srcShelfStatusDesc,
st.usable_quantity as usableQuantity FROM saas_stock st,saas_goods goods,saas_lot_number lot,saas_warehouse_shelf ws WHERE goods.id = st.goods_id AND st.lot_id = lot.id AND ws.id = st.shelf_id AND st.usable_quantity > 0 /** 必须不属于盘点状态 */ AND st.inventory_status = '0' AND st.enterprise_id = #{enterpriseId}
and ( INSTR(goods.name,#{param}) or INSTR(goods.code,#{param}) or INSTR(goods.pinyin_code,#{param}) or INSTR(goods.approval_number,#{param}) or INSTR(goods.barcode,#{param}) or INSTR(goods.generic_name,#{param}))
limit 100

/** 锁定时查出的商品列表 */ SELECT goods.id as goodsId, goods.code AS goodsCode, goods.name as goodsName, goods.barcode as barcode, goods.generic_name AS goodsGenericName, goods.dosage_id as dosageId, goods.dosage_name as dosageName, goods.specification AS goodsSpecification, goods.unit_id as unitId, goods.unit_id as unitName, goods.manufacturer_id AS manufacturerId, goods.manufacturer AS manufacturer, goods.place as goodsPlace, goods.approval_number as approvalNumber,
lot.lot_num as lotNumber, lot.id as lotId, lot.product_date as productDate, lot.valid_until as validDate,
ws.id as srcShelfId, ws.name as srcShelfName, st.base_order_id as handleId, st.base_order_code as handleCode, st.lock_quantity as usableQuantity FROM saas_stock_lock_record st ,saas_goods goods,saas_lot_number lot,saas_warehouse_shelf ws WHERE goods.id = st.goods_id
AND st.lot_id = lot.id
AND ws.id = st.shelf_id AND st.enterprise_id = #{enterpriseId} AND st.lock_quantity > 0 AND st.base_order_type = #{type}
and ( INSTR(goods.name,#{param}) or INSTR(goods.code,#{param}) or INSTR(goods.pinyin_code,#{param}) or INSTR(goods.approval_number,#{param}) or INSTR(goods.barcode,#{param}) or INSTR(goods.generic_name,#{param}))

/** 搜寻货位移动报表（总部） */ SELECT d.id as id, u.code as enterpriseCode, u.name as enterpriseName, d.move_date as moveDate, d.move_code as code, m.move_man_name as moveManName, m.receiver_name as receiverName, d.goods_id as goodsId, d.goods_code as goodsCode, d.barcode as barcode, d.goods_name as goodsName, d.goods_generic_name as goodsGenericName, d.dosage_id as dosageId, d.dosage_name as dosageName, d.goods_specification as goodsSpecification, d.unit_id as unitId, d.unit_name as unitName, d.manufacturer_id as manufacturerId, d.manufacturer as manufacturer, d.goods_place as goodsPlace, d.approval_number as approvalNumber, d.lot_number as lotNum, d.product_date as productDate, d.valid_date as validDate, d.src_shelf_id as srcShelfId, d.src_shelf_name as srcShelfName, d.src_shelf_status_desc as srcShelfStatusDesc, d.target_shelf_id as targetShelfId, d.target_shelf_name as targetShelfName, d.target_shelf_status_desc as targetShelfStatusDesc, d.quantity as quantity, d.remark as remark, m.status as status, g.business_variety as businessVariety, g.category_id as categoryId, ( SELECT cate.name FROM saas_goods_category cate WHERE cate.id = g.category_id ) AS categoryName, g.goods_attribute as goodsAttribute, g.prescription_drug as prescriptionDrug, g.otc_type as otcType, g.cosmetics as cosmetics, g.domestic_import as domesticImport, g.management_scope_id as managementScopeId, g.management_scope_name as managementScopeName, g.special_drug as specialDrug, g.spirit_drug_type as spiritDrugType, g.formulation_type as formulationType, g.in_charge_drug as inChargeDrug, g.limit_quantity as limitQuantity, g.storage_temp as storageTemp, g.storage_condition_name as storageConditionName, g.quality_period as qualityPeriod, g.quality_period_unit as qualityPeriodUnit FROM saas_shelf_move_detail d LEFT JOIN saas_shelf_move m ON d.move_id = m.id LEFT JOIN saas_enterprise u ON u.id = d.enterprise_id LEFT JOIN saas_goods g ON g.id = d.goods_id WHERE (u.id = #{enterpriseId} or u.parent_id = #{enterpriseId})
and g.business_variety = #{businessVariety}
and ( INSTR(d.goods_name,#{param}) or INSTR(d.goods_code,#{param}) or INSTR(d.approval_number,#{param}) or INSTR(d.barcode,#{param}) or INSTR(d.goods_generic_name,#{param}) )
and u.chain_type = #{chainType}
and INSTR(u.code,#{enterpriseCode})
and INSTR(u.name,#{enterpriseName})
/** 起始时间----终止时间 */
and d.move_date >= #{startTime}
and d.move_date <= #{endTime}
and INSTR(d.move_code,#{code})
and INSTR(m.move_man_name,#{moveManName})
and INSTR(m.receiver_name,#{receiverName})
and g.category_id = #{categoryId}
and g.domestic_import = #{domesticImport}
and g.storage_temp = #{storageTemp}
and INSTR(g.storage_condition_name,#{storageConditionName})
and INSTR(g.registered_trademark,#{registeredTrademark})
and INSTR(g.brand,#{brand})
and g.goods_attribute = #{goodsAttribute}
and g.prescription_drug = #{prescriptionDrug}
and g.otc_type = #{otcType}
and g.management_scope_id = #{managementScopeId}
and g.special_drug = #{specialDrug}
and g.in_charge_drug = #{inChargeDrug}
and g.spirit_drug_type = #{spiritDrugType}
and g.formulation_type = #{formulationType}
order by d.${order} ${sort}
order by d.id desc
limit #{start},#{pageSize}

/** 搜寻货位移动报表（分部） */ SELECT d.id as id, d.move_date as moveDate, d.move_code as code, m.move_man_name as moveManName, m.receiver_name as receiverName, d.goods_id as goodsId, d.goods_code as goodsCode, d.barcode as barcode, d.goods_name as goodsName, d.goods_generic_name as goodsGenericName, d.dosage_id as dosageId, d.dosage_name as dosageName, d.goods_specification as goodsSpecification, d.unit_id as unitId, d.unit_name as unitName, d.manufacturer_id as manufacturerId, d.manufacturer as manufacturer, d.goods_place as goodsPlace, d.approval_number as approvalNumber, d.lot_number as lotNum, d.product_date as productDate, d.valid_date as validDate, d.src_shelf_id as srcShelfId, d.src_shelf_name as srcShelfName, d.src_shelf_status_desc as srcShelfStatusDesc, d.target_shelf_id as targetShelfId, d.target_shelf_name as targetShelfName, d.target_shelf_status_desc as targetShelfStatusDesc, d.quantity as quantity, d.remark as remark, m.status as status, g.business_variety as businessVariety, g.category_id as categoryId, ( SELECT cate.name FROM saas_goods_category cate WHERE cate.id = g.category_id ) AS categoryName, g.goods_attribute as goodsAttribute, g.prescription_drug as prescriptionDrug, g.otc_type as otcType, g.cosmetics as cosmetics, g.domestic_import as domesticImport, g.management_scope_id as managementScopeId, g.management_scope_name as managementScopeName, g.special_drug as specialDrug, g.spirit_drug_type as spiritDrugType, g.formulation_type as formulationType, g.in_charge_drug as inChargeDrug, g.limit_quantity as limitQuantity, g.storage_temp as storageTemp, g.storage_condition_name as storageConditionName, g.quality_period as qualityPeriod, g.quality_period_unit as qualityPeriodUnit FROM saas_shelf_move_detail d LEFT JOIN saas_shelf_move m ON d.move_id = m.id LEFT JOIN saas_goods g ON g.id = d.goods_id WHERE d.enterprise_id = #{enterpriseId}
and g.business_variety = #{businessVariety}
and ( INSTR(d.goods_name,#{param}) or INSTR(d.goods_code,#{param}) or INSTR(d.approval_number,#{param}) or INSTR(d.barcode,#{param}) or INSTR(d.goods_generic_name,#{param}) )
/** 起始时间----终止时间 */
and d.move_date >= #{startTime}
and d.move_date <= #{endTime}
and INSTR(d.move_code,#{code})
and INSTR(m.move_man_name,#{moveManName})
and INSTR(m.receiver_name,#{receiverName})
and g.category_id = #{categoryId}
and g.domestic_import = #{domesticImport}
and g.storage_temp = #{storageTemp}
and INSTR(g.storage_condition_name,#{storageConditionName})
and INSTR(g.registered_trademark,#{registeredTrademark})
and INSTR(g.brand,#{brand})
and g.goods_attribute = #{goodsAttribute}
and g.prescription_drug = #{prescriptionDrug}
and g.otc_type = #{otcType}
and g.management_scope_id = #{managementScopeId}
and g.special_drug = #{specialDrug}
and g.in_charge_drug = #{inChargeDrug}
and g.spirit_drug_type = #{spiritDrugType}
and g.formulation_type = #{formulationType}
order by d.${order} ${sort}
order by d.id desc
limit #{start},#{pageSize}

SELECT
sum(d.quantity)
FROM saas_shelf_move_detail d
LEFT JOIN saas_shelf_move m
ON d.move_id = m.id
LEFT JOIN saas_enterprise u
ON u.id = d.enterprise_id
LEFT JOIN saas_goods g
ON g.id = d.goods_id
WHERE (u.id = #{enterpriseId} or u.parent_id = #{enterpriseId})

  and g.business_variety = #{businessVariety}


  and (
  INSTR(d.goods_name,#{param})
  or INSTR(d.goods_code,#{param})
  or INSTR(d.approval_number,#{param})
  or INSTR(d.barcode,#{param})
  or INSTR(d.goods_generic_name,#{param})
  )


  and u.chain_type = #{chainType}


  and INSTR(u.code,#{enterpriseCode})


  and INSTR(u.name,#{enterpriseName})

/**
起始时间----终止时间
*/

  and d.move_date >= #{startTime}


  and d.move_date <= #{endTime}


  and INSTR(d.move_code,#{code})


  and INSTR(m.move_man_name,#{moveManName})


  and INSTR(m.receiver_name,#{receiverName})


  and g.category_id = #{categoryId}


  and g.domestic_import = #{domesticImport}


  and g.storage_temp = #{storageTemp}


  and INSTR(g.storage_condition_name,#{storageConditionName})


  and INSTR(g.registered_trademark,#{registeredTrademark})


  and INSTR(g.brand,#{brand})


  and g.goods_attribute = #{goodsAttribute}


  and g.prescription_drug = #{prescriptionDrug}


  and g.otc_type = #{otcType}


  and g.management_scope_id = #{managementScopeId}


  and g.special_drug = #{specialDrug}


  and g.in_charge_drug = #{inChargeDrug}


  and g.spirit_drug_type = #{spiritDrugType}


  and g.formulation_type = #{formulationType}


  
    order by d.${order} ${sort}
  
  
    order by d.id desc

