mapper.select:
SELECT goo.id AS goodsId, goo.code AS goodsCode, goo.name AS goodsName, goo.barcode AS barcode, goo.generic_name AS goodsGenericName, goo.dosage_id AS dosageId, goo.dosage_name AS dosageName, goo.specification AS goodsSpecification, goo.unit_id AS unitId, goo.unit_id AS unitName, goo.manufacturer_id AS manufacturerId, goo.manufacturer AS manufacturer, goo.place AS goodsPlace, goo.approval_number AS approvalNumber, lot.lot_num AS lotNumber, lot.id AS lotId, lot.product_date AS productDate, lot.valid_until AS validDate, ws.id AS srcShelfId, ws.name AS srcShelfName, st.shelf_id AS shelfId, st.shelf_name AS shelfName, st.shelf_status_desc AS shelfStatusDesc, st.usable_quantity AS quantity, ss.split_goods_id AS splitGoodsId, goo.code as splitGoodsCode, goo.name as splitGoodsName, ss.split_specification AS splitSpecification, ss.split_unit_name AS splitUnitName, ss.split_quantity AS splitQuantity, ss.split_shelf_id AS splitShelfId, ss.split_shelf_name AS splitShelfName FROM saas_split_set ss, saas_goods goo, saas_stock st, saas_lot_number lot , saas_warehouse_shelf ws, saas_warehouse_cargo_area area WHERE ss.goods_id = goo.id AND
ss.enterprise_id = #{enterpriseId}
( ss.enterprise_id = #{enterpriseId} or ss.enterprise_id = #{currentEId} )
and goo.id = st.goods_id AND st.lot_id = lot.id AND ws.id = st.shelf_id AND ws.cargo_area_id = area.id AND area.job_type = 0 AND area.job_area = 0 AND st.usable_quantity > 0 AND st.enterprise_id = #{currentEId}
and (goo.name like concat('%', #{param},'%') or goo.code like concat('%', #{param},'%') or goo.pinyin_code like concat('%', #{param},'%') or goo.approval_number like concat('%', #{param},'%') or goo.barcode like concat('%', #{param},'%') or goo.generic_name like concat('%', #{param},'%') or INSTR(lot.lot_num,#{param}) or INSTR(ws.name,#{param}) )

SELECT id AS id, date_format(split_date, '%Y-%m-%d') AS splitDate, code AS code, split_man_id AS splitManId, split_man_code AS splitManCode, split_man_name AS splitManName, audit_man_id AS auditManId, audit_man_code AS auditManCode, audit_man_name AS auditManName, remark AS remark FROM saas_split WHERE enterprise_id = #{enterpriseId}
and split_date between #{startTime} and #{endTime}
AND code like concat('%', #{code},'%')
AND split_man_name like concat('%', #{splitManName},'%')
AND audit_man_name like concat('%', #{auditManName},'%')
order by ${order} ${sort}
order by id desc

/** 搜寻商品拆零报表（总部） */ SELECT d.id as id, u.code as enterpriseCode, u.name as enterpriseName, d.split_date as splitDate, d.split_code as code, s.split_man_name as splitManName, s.audit_man_name as auditManName, s.flow_code as flowCode, d.goods_id as goodsId, d.goods_code as goodsCode, d.goods_name as goodsName, d.lot_number as lotNum, d.product_date as productDate, d.valid_date as validDate, d.shelf_id as shelfId, d.shelf_name as shelfName, d.shelf_status_desc as shelfStatusDesc, d.quantity as quantity, /** 拆零商品信息 */ d.split_goods_id as splitGoodsId, d.split_goods_code as splitGoodsCode, d.split_shelf_name as splitShelfName, d.split_shelf_status_desc as splitShelfStatusDesc, d.split_quantity as splitQuantity, d.status as status, g.business_variety as businessVariety, g.category_id as categoryId, ( SELECT cate.name FROM saas_goods_category cate WHERE cate.id = g.category_id ) AS categoryName, g.goods_attribute as goodsAttribute, g.prescription_drug as prescriptionDrug, g.otc_type as otcType, g.cosmetics as cosmetics, g.domestic_import as domesticImport, g.management_scope_id as managementScopeId, g.management_scope_name as managementScopeName, g.special_drug as specialDrug, g.spirit_drug_type as spiritDrugType, g.formulation_type as formulationType, g.in_charge_drug as inChargeDrug, g.limit_quantity as limitQuantity, g.storage_temp as storageTemp, g.storage_condition_name as storageConditionName, g.quality_period as qualityPeriod, g.quality_period_unit as qualityPeriodUnit FROM saas_split_detail d LEFT JOIN saas_split s ON s.id = d.split_id LEFT JOIN saas_enterprise u ON u.id = d.enterprise_id LEFT JOIN saas_goods g ON g.id = d.goods_id WHERE (u.id = #{enterpriseId} or u.parent_id = #{enterpriseId})
and g.business_variety = #{businessVariety}
and ( INSTR(g.code,#{param}) or INSTR(g.barcode,#{param}) or INSTR(g.name,#{param}) or INSTR(g.generic_name,#{param}) or INSTR(g.approval_number,#{param}))
and u.chain_type = #{chainType}
and INSTR(u.code,#{enterpriseCode})
and INSTR(u.name,#{enterpriseName})
/** 起始时间----终止时间 */
and d.split_date >= #{startTime}
and d.split_date <= #{endTime}
and INSTR(d.split_code,#{code})
and INSTR(s.split_man_name,#{splitManName})
and INSTR(s.audit_man_name,#{auditManName})
and INSTR(s.flow_code,#{flowCode})
and s.status = #{status}
and g.category_id = #{categoryId}
and g.domestic_import = #{domesticImport}
and g.storage_temp = #{storageTemp}
and INSTR(g.storage_condition_name,#{storageConditionName})
and INSTR(g.registered_trademark,#{registeredTrademark})
and INSTR(g.brand,#{brand})
and g.goods_attribute = #{goodsAttribute}
and g.prescription_drug = #{prescriptionDrug}
and g.otc_type = #{otcType}
and g.management_scope_id = #{managementScopeId}
and g.special_drug = #{specialDrug}
and g.in_charge_drug = #{inChargeDrug}
and g.spirit_drug_type = #{spiritDrugType}
and g.formulation_type = #{formulationType}
order by d.${order} ${sort}
order by d.id desc
limit #{start},#{pageSize}

/** 搜寻商品拆零报表（分部） */ SELECT d.id as id, d.split_date as splitDate, d.split_code as code, s.split_man_name as splitManName, s.audit_man_name as auditManName, s.flow_code as flowCode, d.goods_id as goodsId, d.goods_code as goodsCode, d.goods_name as goodsName, d.lot_number as lotNum, d.product_date as productDate, d.valid_date as validDate, d.shelf_id as shelfId, d.shelf_name as shelfName, d.shelf_status_desc as shelfStatusDesc, d.quantity as quantity, /** 拆零商品信息 */ d.split_goods_id as splitGoodsId, d.split_goods_code as splitGoodsCode, d.split_shelf_name as splitShelfName, d.split_shelf_status_desc as splitShelfStatusDesc, d.split_quantity as splitQuantity, d.status as status, g.business_variety as businessVariety, g.category_id as categoryId, ( SELECT cate.name FROM saas_goods_category cate WHERE cate.id = g.category_id ) AS categoryName, g.goods_attribute as goodsAttribute, g.prescription_drug as prescriptionDrug, g.otc_type as otcType, g.cosmetics as cosmetics, g.domestic_import as domesticImport, g.management_scope_id as managementScopeId, g.management_scope_name as managementScopeName, g.special_drug as specialDrug, g.spirit_drug_type as spiritDrugType, g.formulation_type as formulationType, g.in_charge_drug as inChargeDrug, g.limit_quantity as limitQuantity, g.storage_temp as storageTemp, g.storage_condition_name as storageConditionName, g.quality_period as qualityPeriod, g.quality_period_unit as qualityPeriodUnit FROM saas_split_detail d LEFT JOIN saas_split s ON s.id = d.split_id LEFT JOIN saas_goods g ON g.id = d.goods_id WHERE d.enterprise_id = #{enterpriseId}
and g.business_variety = #{businessVariety}
and ( INSTR(g.code,#{param}) or INSTR(g.barcode,#{param}) or INSTR(g.name,#{param}) or INSTR(g.generic_name,#{param}) or INSTR(g.approval_number,#{param}))
/** 起始时间----终止时间 */
and d.split_date >= #{startTime}
and d.split_date <= #{endTime}
and INSTR(d.split_code,#{code})
and INSTR(s.split_man_name,#{splitManName})
and INSTR(s.audit_man_name,#{auditManName})
and INSTR(s.flow_code,#{flowCode})
and s.status = #{status}
and g.category_id = #{categoryId}
and g.domestic_import = #{domesticImport}
and g.storage_temp = #{storageTemp}
and INSTR(g.storage_condition_name,#{storageConditionName})
and INSTR(g.registered_trademark,#{registeredTrademark})
and INSTR(g.brand,#{brand})
and g.goods_attribute = #{goodsAttribute}
and g.prescription_drug = #{prescriptionDrug}
and g.otc_type = #{otcType}
and g.management_scope_id = #{managementScopeId}
and g.special_drug = #{specialDrug}
and g.in_charge_drug = #{inChargeDrug}
and g.spirit_drug_type = #{spiritDrugType}
and g.formulation_type = #{formulationType}
order by d.${order} ${sort}
order by d.id desc
limit #{start},#{pageSize}

SELECT
count(*)
FROM saas_split_detail d
LEFT JOIN saas_split s
ON s.id = d.split_id
LEFT JOIN saas_enterprise u
ON u.id = d.enterprise_id
LEFT JOIN saas_goods g
ON g.id = d.goods_id
WHERE (u.id = #{enterpriseId} or u.parent_id = #{enterpriseId})

    and g.business_variety = #{businessVariety}


    and (
    INSTR(g.code,#{param})
    or INSTR(g.barcode,#{param})
    or INSTR(g.name,#{param})
    or INSTR(g.generic_name,#{param})
    or INSTR(g.approval_number,#{param}))


    and u.chain_type = #{chainType}


    and INSTR(u.code,#{enterpriseCode})


    and INSTR(u.name,#{enterpriseName})

/**
起始时间----终止时间
*/

    and d.split_date >= #{startTime}


    and d.split_date <= #{endTime}


    and INSTR(d.split_code,#{code})


    and INSTR(s.split_man_name,#{splitManName})


    and INSTR(s.audit_man_name,#{auditManName})


    and INSTR(s.flow_code,#{flowCode})


    and s.status = #{status}


    and g.category_id = #{categoryId}


    and g.domestic_import = #{domesticImport}


    and g.storage_temp = #{storageTemp}


    and INSTR(g.storage_condition_name,#{storageConditionName})


    and INSTR(g.registered_trademark,#{registeredTrademark})


    and INSTR(g.brand,#{brand})


    and g.goods_attribute = #{goodsAttribute}


    and g.prescription_drug = #{prescriptionDrug}


    and g.otc_type = #{otcType}


    and g.management_scope_id = #{managementScopeId}


    and g.special_drug = #{specialDrug}


    and g.in_charge_drug = #{inChargeDrug}


    and g.spirit_drug_type = #{spiritDrugType}


    and g.formulation_type = #{formulationType}


    
        order by d.${order} ${sort}
    
    
        order by d.id desc

