	    AND (e.approve_status = 0 or e.approve_status = -1)
	
      
          and  e.approve_status = #{approveStatus,jdbcType=INTEGER}
      

	  
	    AND (e.code like '%${key}%'
	    	or e.pinyin like '%${key}%'
	    	or e.name like '%${key}%'
	    )
	
	  
    	order by ${order} ${sort}
    
	limit #{start},#{pageSize}

