mapper.update:
UPDATE saas_enterprise set

    store_level_id = #{businessId}


    sale_circle_id = #{businessId}


    sale_area_id = #{businessId}


    group_id = #{businessId}

where parent_id = #{enterpriseId}

   and id = #{storeId}

UPDATE saas_enterprise set

    store_level_id = 0


    sale_circle_id = 0


    sale_area_id = 0


    group_id = 0

where parent_id = #{enterpriseId}

   and id = #{storeId}


    and store_level_id = #{businessId}


    and sale_circle_id = #{businessId}


    and sale_area_id = #{businessId}


    and group_id = #{businessId}

mapper.select:
select e.id, e.code, e.name from saas_enterprise e ,saas_enterprise_business b where e.id = b.enterprise_id and e.parent_id = #{enterpriseId} and status = 1 and b.member_info = 0
AND (e.code like '%${key}%' or e.pinyin like '%${key}%' or e.name like '%${key}%' )
order by code ${sort}

SELECT id AS id, code AS code, name AS name FROM saas_enterprise WHERE parent_id = #{id,jdbcType=BIGINT}
and (name like concat('%', #{param},'%') or code like concat('%', #{param},'%'))
and status = '1' AND valid_flag = '1'

SELECT
 
 from saas_enterprise
 WHERE id = #{enterpriseId} or parent_id = #{enterpriseId}

